import React from "react";
import axios from 'axios';
import {Link} from 'react-router-dom';



export default class Productlist extends React.Component {
    state = {
        products: [],
        type: "All",
        filterText: ""
    }

    //UseState is a react hook that lets you set some state to react component.

    componentDidMount() {
        axios.get("http://localhost:3001/Products/hightoLow") 
        .then(res => {
            console.log(res);
            this.setState({products: res.data});
        });
    }

    myFilter (type) {
        return () => {
            this.setState({type})
        }
    }


    onChange (e){
        this.setState({filterText: e.target.value})

    }

    normalizeString(str) {
        return str.toLowerCase().replace(/\s/g, "");
    }

    render() {

        return (

            <div>
                <div className="product-container">
                <div className="search-container">
                <h3 className="searchLabel">Search By Name:</h3>
                <div>
                    <input className="searchBar" type="text" value={this.state.filterText} onChange={(e) => this.onChange(e)}></input>
                </div>
                </div>
                <div id="filter-container">
            <h4>Filter By Category: </h4>          
                    <button class="btn btn-dark" onClick={this.myFilter("All")}>All</button>
                    <button class="btn btn-dark" onClick={this.myFilter("gaming")}>Xbox</button>
                    <button class="btn btn-dark" onClick={this.myFilter("studio")}>PlayStation</button>
                    <button class="btn btn-dark" onClick={this.myFilter("everyday")}>Nintendo</button>
            <h4>Sort By Price: </h4> 
                    <Link to ='/products/LowtoHigh' class="btn btn-dark">Low to High</Link>
                    <Link to ='/products/HightoLow' class="btn btn-dark">High to Low</Link>
                    <Link to ='/products' class="btn btn-dark">Reset</Link>
                </div>
                </div>
                <div className="cards-container">
                    {
                        this.state.products 
                        .filter((props) => {
                            if (this.state.filterText === ""){
                                return true
                            } else {
                                return this.normalizeString(props.prodName).includes(this.normalizeString(this.state.filterText))
                            }
                        })

                        .filter((record) => {
                            if (this.state.type === "All"){
                                return true 
                            } else {
                                return this.state.type === record.Category
                            }

                        })

                        .map(props => (
                          <section class="card">
               <div className="products" key={props.id}>
              <img className="img" src={props.image} alt={props.device} />
              <p className="console">{props.product}</p>
              <p className="descriptions">{props.descriptions}</p>
              <p className="price">${props.price}</p>
            </div>
             <div class="btn-2">Add to cart</div>
            </section>   
                        ))
                    }
                </div>
            </div>
        )
    }
}
