import React from "react";
import axios from "axios";
import './products.css'
import Filters from "../component/filters"

class Products extends React.Component {
  state= {
    products: [],

  }
  componentDidMount() {
      axios.get("http://localhost:3001/Products").then((res) => {
        const Products = res.data;
        this.setState({products: Products });
      });
    }
      render() {
    return (
      <>
          <div>
        <div className="filter_btns">
          <button className="all_btn" onClick={() => Products()}>
            All
          </button>
          <button className="tops_btn" onClick={() => Products("tops")}>
            Tops
          </button>
          <button
            className="bottoms_btn"
            onClick={() => getProducts("bottoms")}
          >
            Bottoms
          </button>
          <button
            className="accessory_btn"
            onClick={() => getProducts("accessories")}
          >
            Accessories
          </button>
          <button className="shoes_btn" onClick={() => getProducts("shoes")}>
            Shoes
          </button>
        </div>
    <div className="product">
          {this.state.products.map((items) => {
            return (

              <div className="products">
                <img className="image" src={items.image} alt={items.name} />
                <h2 className="console">{items.product}</h2>
                <h2 className="descriptions">{items.descriptions}</h2>
                <p className="price">${items.price}</p>
                <a href="contact.html" className="button">
                  ADD TO CART
                </a>
              </div>

            );
          })}
        </div>

        </div>
      </>

    );
  }
}
export default Products;
