import React from "react";
import './form.css'
const Form = () => {
  return (
   <div class="form-container">
        <div class="contact-box">
          <div class="left"></div>
          <div class="right">
            <h2>Contact Us</h2>
            <form>
            <input type="text" class="field" placeholder="Full Name" required />
            <input type="email" class="field" placeholder="Email" required />
            <input type="text" class="field" placeholder="Phone Number" required />
            <textarea class="field area" placeholder="Message"></textarea>
            <button class="btn">Send</button>
            </form>
          </div>
        </div>
      </div>
  );
};

export default Form;
