import React from "react";
import axios from "axios";

class Product extends React.Component {
  constructor(props) {
    super(props);
    this.state = { product };
  }
  componentDidMount() {
    fetch(
      "http://localhost:3001/Products" //{method: 'GET',
        //  mode: 'no-cors',
        //  headers: {
        //    'Content-Type': 'application/json'
        //  }
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(data); // { message: 'Hello from the server!' }
      });
  }
  render() {
    return (
      <div>
        <ul>
          {this.state.product.map((items) => (
            <p>
              <li>{items.device}</li>
              <li>{items.description}</li>
              <li>{items.price}</li>
              <li>{items.images}</li>
            </p>
          ))}
        </ul>
      </div>
    );
  }
}
export default Product;
